export default function Card(personaje) {

   return (

      <div style={{
         // backgroundColor: '#48C9B0',
         backgroundColor: 'white',
         margin: '5px',
         maxWidth: '20%',
         // position: 'relative',
         justifySelf: 'center',
         borderRadius: 20,
         fontSize: '0.6em',
         boxShadow: '0px 2px 4px',
         flexBasis: '200px', /* Ancho base de cada elemento */
  flexGrow: 1, /* Permitir crecer para llenar el espacio disponible */
  flexShrink: 0, /* Evitar que los elementos se contraigan más allá de su tamaño base */

      }}>
         <button style={{
            position: 'absolute',
            right: 0,
            border: 'none',
            backgroundColor: 'white',
            


         }} onClick={() => personaje.onClose()}>X</button>
         <img style={{
            margin: '5px',
            maxWidth: '90%',
            borderRadius: 20,
            marginTop: '5%',
            boxShadow: '0px 2px 4px'
         }} src={personaje.image} alt={personaje.name} />
         <div >
            <div style={{
               backgroundColor:'yellow',
               width: '70%',
               position: 'absolute',
               top: '40%',
               margin:'15px',
               height: '30px',
               borderRadius:20
               
            }}>
               <h2>{personaje.name}</h2>
            </div>
            <div >
               <h2>{personaje.status}</h2>
               <h2>{personaje.species}</h2>
               <h2>{personaje.gender}</h2>
            </div>
         </div>
      </div>
   );
}
