import SearchBar from "../SearchBar";
import { Link } from "react-router-dom";
import Logo from "../../assets/logo.png";
import { useLocation } from "react-router-dom";
export default function Nav(props) {
    const isRaiz = useLocation().pathname === '/';
    return (

        <div style={{
            width: '100%',
            backgroundColor: '#B9E5D3',
            display: 'flex',
            position: 'relative',
            alignItems: 'center',
            borderBottom: '2px solid #36E2C5',

        }}>
            {isRaiz ? null :
                <>
                    <img src={Logo} alt='Rick_And_Morty' style={{ width: 250, marginLeft: 15, marginRight: '10%' }} />
                    <div ><SearchBar onSearch={props.onSearch} /></div>
                    <div style={{
                        display: 'flex',
                        position: 'absolute',
                        right: '30%',

                    }}>
                        <div
                            style={{
                                margin: 10,
                            }}><Link to={"/home"} style={{
                                textDecoration: 'none',
                                fontWeight: 'bold',
                                color: 'black',
                            }}><div >Home</div></Link></div>
                        <div style={{
                            margin: 10,
                        }}><Link to={"/about"} style={{
                            fontWeight: 'bold',
                            textDecoration: 'none',
                            color: 'black',
                        }}><div>About</div></Link></div>
                    </div>
                </>}
        </div>
    );

}
