export default function Card(personaje) {

   return (
      
      <div style={{
         backgroundColor:'#48C9B0',
         margin:'5px',
         maxWidth:'20%',
         position: 'relative',
         justifySelf:'center',
         borderRadius: 20,
         fontSize: '0.8em'
        
         
      }}>
          <button style={{
             position: 'absolute',
             right:0,
             
          }}onClick={()=>personaje.onClose()}>X</button>
         <img style={{
            margin:'5px',
            maxWidth:'90%',
            borderRadius: 20,
            marginTop:'10%'
            }} src={personaje.image} alt={personaje.name} />
         <h2>{personaje.name}</h2>
         <h2>{personaje.status}</h2>
         <h2>{personaje.species}</h2>
         <h2>{personaje.gender}</h2>
         <h2>{personaje.origin.name}</h2>
      </div>
   );
}
