const { ADD_FAV, REMOVE_FAV, FILTER, ORDER } = require('../actions/types');
const inicialState = {
    allCharacters: [],
}


const rootReducer = (state = inicialState, actions) => {
    switch (actions.type) {
        case LOGIN:

            return {
                ...state,
                sesion: { ...state.sesion, status: actions.payload },
            }

        case LOGOUT:
            return {
                ...state,
                status: false,
            }

        default: return { ...state }
    }
}

export default rootReducer;
