import { useParams } from "react-router-dom";
import styles from './Detail.module.css';

export default function Detail({ characters }) {
    const { id } = useParams();
    let personaje = characters.filter((personaje) => personaje === id)[0];
    return (
        <div style={{
            position: 'relative',
        }}>
            <h1>SOY DETAIL ID: {id}</h1>
            <div style={{
                left: '50%',
                backgroundColor: 'blue',
                position: 'absolute',
                width: '30em',
                height: '30em',
                transform: 'translate(-50%, -10%)'
            }}>
                <div>
                    <img src={characters.img} alt="" />
                </div>
                <div>
                    <h2 className={styles.name}>{characters.name}</h2>
                    <h2 className={styles.specie}>{characters.species}</h2>
                    <h2 className={styles.gender}>{characters.gender}</h2>
                </div>
            </div>
        </div>
    );
};
