const { ADD_FAV, REMOVE_FAV, FILTER, ORDER } = require('../actions/types');
const inicialState = {
    myFavorites: [],
    allCharacters: [],
}


const rootReducer = (state = inicialState, { type, payload }) => {
    switch (type) {
        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavorites, payload],
                allCharacters: [...state.allCharacters, payload]
            }

        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(personaje => personaje.id !== Number(payload))
            }
        case FILTER:
            const filterGender = [...state.allCharacters].filter(personaje => personaje.gender === payload);
            return {
                ...state,
                allCharacters: filterGender,
                myFavorites: filterGender,
            }
        case ORDER:
            const newArray = [...myFavorites];
            if (payload.toUpperCase() === "A") {
                newArray = [...myFavorites].sort((a, b) => {
                    a.nombre.toUpperCase().chartCodeAt(0) - b.nombre.toUpperCase(0);
                });
            } else if (payload.toUpperCase() === "D") {
                newArray = [...myFavorites].sort((a, b) => {
                    b.nombre.toUpperCase().chartCodeAt(0) - a.nombre.toUpperCase(0);
                });
            }
            return {
                ...state,
                status: false,
            }

        default: return { ...state }
    }
}

export default rootReducer;
