const { LOGIN, LOGOUT } = require('../actions/types');
const inicialState = {
    sesion: {
        email: "ing.rafaelprieto@gmail.com",
        password: "Rafael1234",
        status: false,
    }
}


const rootReducer = (state = inicialState, actions) => {
    switch (actions.type) {
        case LOGIN:
            let resul = (actions.payload.email === state.sesion.email
                && actions.payload.password === state.sesion.password) ? true : false;
                console.log(resul);
            return {
                ...state,
                sesion: {...state.sesion, status: resul},
            }

        case LOGOUT:
            return {
                ...state,
                status: false,
            }

        default: return {...state }
    }
}

export default rootReducer;
