import SearchBar from "../SearchBar";
import { Link } from "react-router-dom";
import Logo from "../../assets/logo.png";
import styles from './Nav.module.css';

export default function Nav(props) {
    const isRaiz = useLocation().pathname === '/home';
    return (
        <div className={styles.container}>
            <img src={Logo} alt='Rick_And_Morty' style={{ width: 250, marginLeft: 15, marginRight: '10%' }} />
            <div ><SearchBar onSearch={props.onSearch} /></div>
            <div style={{
                display: 'flex',
                position: 'absolute',
                right: '20%',

            }}>
                <div style={{
                    margin:5,
                    borderTopLeftRadius: '0.5em 0.5em',
                    borderTopRightRadius: '0.5em 0.5em',
                    backgroundColor: '#2DCFAB',
                    width:65,
                }}><Link to={"/home"} style={{
                        textDecoration: 'none',
                        fontWeight: 'bold',
                        color: 'black',
                    }}><div >Home</div></Link></div>
                <div style={{
                    margin: 5,
                    borderTopLeftRadius: '0.5em 0.5em',
                    borderTopRightRadius: '0.5em 0.5em',
                    backgroundColor: '#2DCF77',
                    width:65,
                }}><Link to={"/about"} style={{
                    fontWeight: 'bold',
                    textDecoration: 'none',
                    color: 'black',
                }}><div >About</div></Link></div>
            </div>
        </div>
    );

}
