import Card from '../Card/Card';
import { useState } from 'react';

export default function Cards({ characters }) {

   const [personajes, setPersonajes] = useState(characters);

   const handleCardClose = (index) => {
      setPersonajes(personajes.filter(i=>i.id!==index))
   };

   return (
      <div style={{
         backgroundColor:'red',
         border:'2px solit white'
      }}>
         {personajes.map(personaje => 
         <Card {...personaje} key={personaje.id} onClose={()=>handleCardClose(personaje.id)}  />)}
      </div>
   )
}
