import React, { useState, useEffect } from "react";
import logo from '../assets/logo.png';
import portada from '../assets/portada.png';
import { useNavigate } from "react-router-dom";
import styles from './Form.module.css'



const Validate =(form)=>{
    const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/;
    let errors={};

    if (!form.name) {
        errors.name = "Se requiere un nombre";
      }
      if (!form.message) {
        errors.message = "Se requiere un mensaje";
      }
    
      if (!regexEmail.test(form.email)) {
        errors.email = "Debe ser un correo electr칩nico";
      }
      return errors;

}
export default function Form(sesion) {
    const navegate = useNavigate();

    const [inputs, setInputs] = useState({
        email: "",
        password: "",
    });

    const [errors, setErrors] = useState({
        email: "",
        password: "",
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setInputs({...inputs,[name]: value});
        setErrors(validate({...inputs,[name]: value}));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        if(sesion.login(inputs)){
            navegate('/home');
        }else{
            alert("Email y/o password incorrecto");
        }
    }

    return (
        <div className={styles.container}>
            <div className={styles.containerportada}>
                <img img src={portada} className={styles.imgportada} />
                <img src={logo} className={styles.imglog} />
            </div>

            <div className={styles.containerForm}>
                <form onSubmit={handleSubmit} className={styles.form}>
                    <label htmlFor='email' style={{
                        position: 'absolute',
                        left: 54,
                        width: 300,
                        textAlign: 'start',
                        top: 5
                    }}>Email:</label>
                    <br />
                    <input name='email' value={inputs.email} onChange={handleChange} type='email' placeholder='Escribe tu email...'
                        style={{
                            position: 'absolute',
                            left: 52,
                            width: 288,
                            height: 20,
                            top: 30
                        }}></input>
                    {errors.email && <p className='danger'>{errors.email}</p>}
                    <br />
                    <label htmlFor='password'
                        style={{
                            position: 'absolute',
                            width: 288,
                            left: 54,
                            textAlign: 'start',
                            top: 60
                        }}>Contrase침a:</label>
                    <input name='password' value={inputs.password} onChange={handleChange} type='password' placeholder='Escribe tu contrase침a...'
                        style={{
                            position: 'absolute',
                            bottom: 70,
                            left: 52,
                            width: 288,
                            height: 20,
                            justifyItems: 'center',
                            alignSelf: 'center',
                        }}></input>

                    <button className={styles.btn} type='submit'>Entrar</button>
                </form>
            </div>
        </div>
    )
}
