import { useParams } from "react-router-dom";
import styles from './Detail.module.css';

export default function Detail({ characters }) {
    const { id } = useParams();
    let personaje = characters.filter((personaje) => personaje.id === Number(id))[0];
    return (
        <div style={{
            position: 'relative',
        }}>
         
            <div style={{
                left: '50%',
                backgroundColor: 'blue',
                position: 'absolute',
                width: '30em',
                height: '31em',
                transform: 'translate(-50%)'
            }}>
                   {/* <h1>{personaje.name}</h1> */}
                <div>
                    <img src={personaje.image} alt={personaje.name} style={{margin:10}}/>
                </div>
                <div>
                    <h2 className={styles.name}>{personaje.name}</h2>
                    <h2 className={styles.specie}>{personaje.species}</h2>
                    <h2 className={styles.gender}>{personaje.gender}</h2>
                </div>
            </div>
        </div>
    );
};
