import SearchBar from "../SearchBar";
import { Link } from "react-router-dom";
import Logo from "../../assets/logo.png";
import styles from './Nav.module.css';
import { useLocation } from "react-router-dom";

export default function Nav(props) {
    const ishome = useLocation().pathname === '/home';
    return (
        <div className={styles.container}>
            <img src={Logo} alt='Rick_And_Morty' className={styles.logo} />
            {ishome ? <div ><SearchBar onSearch={props.onSearch} /></div> : null}
            <div className={styles.containerlink}>
                <div className={styles.backgroundlinkhome}>
                    <Link to={"/home"} className={styles.link}>
                        <div >Home</div>
                    </Link></div>
                <div className={styles.backgroundlinkabout}>
                    <Link to={"/about"} className={styles.link}>
                        <div>About</div>
                    </Link>
                </div>
            </div>
        </div>
    );

}
