import React, { useState } from "react"
export default Form = () => {
    const [inputs, setInputs] = useState({
        email: "",
        password: "",
    });
    const handleChange = (event) => {
        const property = event.target.name;
        const value = event.target.value;
        setInputs({
            ...inputs,
            [property]: value,
        });
    }
    return <div>
        <form onSubmit={handleSubmit}>
            {errors.name && <p className='danger'>{errors.name}</p>}
            <label htmlFor='email'>Correo Electrónico:</label>
            <input name='email' value={inputs.email} onChange={handleChange} type='text' placeholder='Escribe tu email...'></input>
            {errors.email && <p className='danger'>{errors.email}</p>}
            <label htmlFor='password'>Contraseña:</label>
            <textarea name='password' value={inputs.password} onChange={handleChange} className={errors.password && 'warning'} type='text' placeholder='Escribe tu constraseña...'></textarea>
            {errors.password && <p className='danger'>{errors.password}</p>}
            <button type='submit'>Enviar</button>
        </form>
    </div>
}
