import { useParams } from "react-router-dom";
import styles from './Detail.module.css';

export default function Detail(personaje) {
    const {id } = useParams();
    console.log(id.personaje);
    return (
        <div style={{
            position: 'relative',
        }}>
            <h1>SOY DETAIL ID: {id}</h1>
            <div style={{
                left: '50%',
                backgroundColor: 'blue',
                position: 'absolute',
                width: '30em',
                height: '30em',
                transform: 'translate(-50%, -10%)'
            }}>
                <div>
                    <img src={personaje.img} alt="" />
                </div>
                <div>
                    <h2 className={styles.name}>{personaje.name}</h2>
                    <h2 className={styles.specie}>{personaje.species}</h2>
                    <h2 className={styles.gender}>{personaje.gender}</h2>
                </div>
            </div>
        </div>
    );
};
