import React, { useState } from "react"
export default function Form() {
    const [inputs, setInputs] = useState({
        email: "",
        password: "",
    });
    const handleChange = (event) => {
        const { name, value } = event.target;
        setInputs({
            ...inputs,
            [name]: value,
        });
    }

    const handleSubmit = () => {

    }
    return (
        <div>

     
        <div style={{

            // position: 'absolute',
            // top: '50%',
            // left: '50%',
            // transform: ' translate(-50%, -50%)',
            // justifyContent: 'center',
            // justifyItems:'center',
            // background: 'linear-gradient(135deg, rgba(46, 205, 207, 0.7), rgba(255, 255, 255, 0.8))',
            // width: 400,
            // height:200, 
            // borderRadius:20,


        }}>
            <form onSubmit={handleSubmit} style={{
                position: 'absolute',
                top: '50%',
                left: '50%',
                transform: ' translate(-50%, -50%)',
                width:'100%'
            }}>
                <label htmlFor='email' style={{
                    backgroundColor:'red',
                    width:100
                }}>Email</label>
                <br/>
                <input name='email' value={inputs.email} onChange={handleChange} type='text' placeholder='Escribe tu email...'
                style={{
                    width:'60%',
                }}></input>
                {/* {errors.email && <p className='danger'>{errors.email}</p>} */}
                <br/>
                <label htmlFor='password'>Contraseña:</label>
                <input name='password' value={inputs.password} onChange={handleChange} type='password' placeholder='Escribe tu constraseña...'></input>
                {/* {errors.password && <p className='danger'>{errors.password}</p>} */}
                <br/>
                <button type='submit'>Enviar</button>
            </form>
        </div>
        </div>
    )
}
