import { Link } from "react-router-dom";
import styles from "./Card.module.css";
export default function Card(personaje) {

   return (

      <div className={styles.container}>
         <button className={styles.buttonCerrar} onClick={() => personaje.onClose()}>X</button>
         <img className={styles.img} src={personaje.image} alt={personaje.name} />
         <div >
            <div className={styles.containerName}>
               <Link to={`/detail/${personaje.id}`} style={{textDecoration:'none',}}>
                  <h2>{personaje.name}</h2>
               </Link>
            </div>
            <div style={{
               // whiteSpace: 'nowrap', /* Evita que los elementos se envuelvan en varias líneas */
            }}>
               <h2 style={{
                  display: 'inline-block', /* Hace que los elementos se coloquen en línea uno al lado del otro */
                  margin: 20, /* Elimina los márgenes predeterminados */
               }}>{personaje.species}</h2>
               <h2 style={{
                  display: 'inline-block', /* Hace que los elementos se coloquen en línea uno al lado del otro */
                  margin: 20, /* Elimina los márgenes predeterminados */
               }}>{personaje.gender}</h2>
            </div>
         </div>
      </div>
   );
}
