import Card from './Card';
import { useState } from 'react';

export default function Cards({ characters }) {

   const [personajes, setPersonajes] = useState(characters);

   const handleCardClose = (index) => {
      let d=personajes.filter(i=>i.id!==index);
      setPersonajes(d)
   };

   

   // onClose={handleCardClose(personaje.id)}
   return (
      <div>
         {characters.map(personaje => <Card {...personaje} 
                                             key={personaje.id}
                                             onClose={()=>handleCardClose(personaje.id)}  />)}
      </div>
   )
}
