import React, { useState } from "react"
export default function Form() {
    const [inputs, setInputs] = useState({
        email: "",
        password: "",
    });
    const handleChange = (event) => {
        const { name, value } = event.target;
        setInputs({
            ...inputs,
            [name]: value,
        });
    }

    const handleSubmit = () => {

    }
    return (

        <div style={{

            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: ' translate( -50%, -50%)',
            justifyContent: 'center',
            justifyItems: 'center',
            background: 'linear-gradient(135deg, rgba(46, 205, 207, 0.7), rgba(255, 255, 255, 0.8))',
            width: 400,
            height: 200,
            borderRadius: 20,
            boxShadow: '0px 2px 4px',
        }}>
            <form onSubmit={handleSubmit} style={{
                position: 'relative',
                top: 10,
                left: '50%',
                transform: ' translate(-50%)',
                width: '100%',
                height:180
            }}>
                <label htmlFor='email' style={{
                    position: 'absolute',
                    left: 50,
                    width: 300,
                    textAlign:'start',
                    top:0
                }}>Email</label>
                <br />
                <input name='email' value={inputs.email} onChange={handleChange} type='text' placeholder='Escribe tu email...'
                    style={{
                        position: 'absolute',
                        left: 52,
                        width: 288,
                        height: 20,
                        top:30
                    }}></input>
                {/* {errors.email && <p className='danger'>{errors.email}</p>} */}
                <br />
                <label htmlFor='password'
                style={{
                    position:'absolute',
                    width: 288,
                    left: 53,
                    textAlign:'start',
                    top:60
                }}>Contraseña:</label>
                <input name='password' value={inputs.password} onChange={handleChange} type='password' placeholder='Escribe tu constraseña...'
                style={{
                    position:'absolute',
                    bottom:70,
                    left:52,
                    width:288,
                    height:20,
                    justifyItems:'center',
                    alignSelf:'center',
                }}></input>
             
                <button style={{
                    position:'absolute',
                    bottom:20,
                    width:300,
                    left:50,
                    borderRadius:8,
                    height:30,
                    backgroundColor:'blue',
                    color:'white',
                   
                }} type='submit'>Enviar</button>
            </form>
        </div>
    )
}
