import { Link } from "react-router-dom";
export default function Card(personaje) {

   return (

      <div style={{
      
         margin: '5px',
         position: 'relative',
         borderRadius: 20,
         fontSize: '0.5em',
         boxShadow: '0px 2px 4px',
         width: 200,
         backgroundColor: 'rgba(143, 228, 229, 0.7)', /* Opacidad del padre: 80% */
color:'white',
      }}>
         <button style={{
            position: 'absolute',
            right: 5,
            top: 5,
            border: 'none',
            backgroundColor: 'transparent',
            cursor: 'pointer',

         }} onClick={() => personaje.onClose()}>X</button>
         <img style={{
            margin: '20px 5px 0px 5px',
            maxWidth: '90%',
            borderRadius: 20,
            boxShadow: '0px 2px 4px',
            backgroundColor: 'rgba(0, 0, 0, 1)', /* Color rojo completamente opaco */
         }} src={personaje.image} alt={personaje.name} />
         <div >
            <div style={{
               backgroundColor: 'black',
               color: 'white',
               width: '80%',
               position: 'absolute',
               top: 160,
               margin: '15px',
               height: '20px',
               borderRadius: '20px',
               left: '50%',
               transform: 'translateX(-60%)',
               backgroundColor: 'rgba(0, 0, 0, 0.7)',

            }}>
               <Link to={`/detail/${personaje.id}`}>
                  <h2 style={{
                     transform: 'translateY(-50%)',
                     color: 'rgba(255, 255, 255, 1)',
                  }}>{personaje.name}</h2>
               </Link>
            </div>
            <div style={{
               // whiteSpace: 'nowrap', /* Evita que los elementos se envuelvan en varias líneas */
            }}>
               <h2 style={{
                  display: 'inline-block', /* Hace que los elementos se coloquen en línea uno al lado del otro */
                  margin: 20, /* Elimina los márgenes predeterminados */
               }}>{personaje.species}</h2>
               <h2 style={{
                  display: 'inline-block', /* Hace que los elementos se coloquen en línea uno al lado del otro */
                  margin: 20, /* Elimina los márgenes predeterminados */
               }}>{personaje.gender}</h2>
            </div>
         </div>
      </div>
   );
}
