export default function Card(personaje) {

   return (

      <div style={{
         // backgroundColor: '#48C9B0',
         backgroundColor: 'white',
         margin: '5px',
         maxWidth: '20%',
         position: 'relative',
         justifySelf: 'center',
         borderRadius: 20,
         fontSize: '0.8em',
         boxShadow: '0px 2px 4px'

      }}>
         <button style={{
            position: 'absolute',
            right: 0,
            border: 'none',
            backgroundColor: 'white',
            


         }} onClick={() => personaje.onClose()}>X</button>
         <img style={{
            margin: '5px',
            maxWidth: '90%',
            borderRadius: 20,
            marginTop: '5%',
            boxShadow: '0px 2px 4px'
         }} src={personaje.image} alt={personaje.name} />
         <div >
            <div style={{
               backgroundColor:'yelow'
            }}>
               <h2>{personaje.name}</h2>
            </div>
            <div >
               <h2>{personaje.status}</h2>
               <h2>{personaje.species}</h2>
               <h2>{personaje.gender}</h2>
            </div>
         </div>
      </div>
   );
}
