import './App.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav.jsx';
import About from './components/About/About';
import Detail from './components/Detail/Detail';
import Form from './Form/Form';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function App() {
   const cantStars = 300;
   const createStart = (top, left) => {
      const container = document.body;
      for (let i = 0; i < cantStars; i++) {
         const star = document.createElement('div');
         star.className = "star";
         star.id = i;
         let size = `${Math.random() * 2}px`;
         let pTop = `${Math.random() * 100}%`;
         let pLeft = `${Math.random() * 100}%`;

         star.style.width = size;
         star.style.height = size;
         star.style.top = pTop;
         star.style.left = pLeft;

         container.appendChild(star);
      }
   }

   const [isBig, setIsBig] = useState(false);
   const [id, setId] = useState(0);
   const changeSize = () => {


      try {
         if (!isBig) {
            let id = parseInt(Math.random() * cantStars);
            const star = document.getElementById(id);
            let w = star.style.width;
            let h = star.style.height;
            star.style.width = `${w + 1}px`;
            star.style.height = `${h + 1}px`;
            setIsBig(true);
            setId(id);
            console.log("BIG: "+isBig, id);
         } else {
            const star = document.getElementById(id);
            let w = star.style.width;
            let h = star.style.height;
            star.style.width = `${w - 1}px`;
            star.style.height = `${h - 1}px`;
            setIsBig(false)
            console.log("SMALL: "+isBig, id);
         }

      } catch (error) {
console.log(error);
      }
      return id;
   }
   const changeSizeSmall = (id) => {
      try {

      } catch (error) {

      }

   }



   useEffect(() => {
      createStart();
      setInterval(changeSize, 2000);
   }, []);
   //  const [scroll, setScroll] =useState();
   //  useEffect(()=>{
   //    console.log(document.documentElement.scroll);
   //  },[]);

   // window.addEventListener("scroll", function () {
   //    let top = document.documentElement.scrollTop;
   //    let left = document.documentElement.scrollLeft;
   //    console.log(`LEFT: ${left} top: ${top}`);
   //    // createStart(top, left);

   //    var viewportWidth = window.innerWidth || document.documentElement.clientWidth;
   //    var viewportHeight = window.innerHeight || document.documentElement.clientHeight;

   //    console.log("Ancho del viewport: " + viewportWidth);
   //    console.log("Alto del viewport: " + viewportHeight);
   //    createStart(viewportHeight, viewportWidth);


   // });



   const navegate = useNavigate();

   const [characters, setCharacters] = useState([]);
   const isRaiz = useLocation().pathname === '/';
   function onSearch(id) {
      if (characters.find(p => p.id === Number(id))) {
         alert("El personaje que intenta mostrar ya está en pantalla");
         return;
      }
      axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('¡No hay personajes con este ID!');
         }
      });
   }

   const onClose = (index) => {
      setCharacters(characters.filter(i => i.id !== index))
   };

   const login = (user) => {
      if (process.env.REACT_APP_EMAIL === user.email && process.env.REACT_APP_PASSWORD === user.password) {
         return true
      }
      return false;
   }

   return (
      <div className='App'>
         {isRaiz ? null : <Nav onSearch={onSearch} />},
         <Routes>
            <Route path='/' element={<Form login={login} />} />
            <Route path='/home' element={
               <Cards characters={characters} onClose={onClose} />
            } />
            <Route path='/about' element={<About />} />
            <Route path='/detail/:id' element={<Detail characters={characters} />} />
         </Routes>
      </div>
   );
}

export default App;
