export default function Card(personaje) {

   return (

      <div style={{
         // backgroundColor: '#48C9B0',
         backgroundColor: 'white',
         margin: '5px',
         position: 'relative',
         borderRadius: 20,
         fontSize: '0.5em',
         boxShadow: '0px 2px 4px',
         width: 200,
         backgroundColor: 'rgba(255, 255, 255, 0.6)', /* Opacidad del padre: 80% */

      }}>
         <button style={{
            position: 'absolute',
            right: 5,
            top: 5,
            border: 'none',
            backgroundColor: 'transparent',
            cursor: 'pointer',

         }} onClick={() => personaje.onClose()}>X</button>
         <img style={{
            margin: '20px 5px 0px 5px',
            maxWidth: '90%',
            borderRadius: 20,
            boxShadow: '0px 2px 4px',
            backgroundColor: 'rgba(0, 0, 0, 1)', /* Color rojo completamente opaco */
         }} src={personaje.image} alt={personaje.name} />
         <div >
            <div style={{
               backgroundColor: 'yellow',
               width: '80%',
               position: 'absolute',
               top: 160,
               margin: '15px',
               height: '20px',
               borderRadius: '20px',
               left: '50%',
               transform: 'translateX(-60%)',

            }}>
               <h2 style={{
                  transform: 'translateY(-50%)',
               }}>{personaje.name}</h2>
            </div>
            <div style={{
               whiteSpace: 'nowrap', /* Evita que los elementos se envuelvan en varias líneas */
            }}>
               <h2 style={{
                  display: 'inline-block', /* Hace que los elementos se coloquen en línea uno al lado del otro */
                  margin: 0, /* Elimina los márgenes predeterminados */
               }}>{personaje.species}</h2>
               <h2 style={{
                  display: 'inline-block', /* Hace que los elementos se coloquen en línea uno al lado del otro */
                  margin: 0, /* Elimina los márgenes predeterminados */
               }}>{personaje.gender}</h2>
            </div>
         </div>
      </div>
   );
}
