    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label htmlFor='email'>Correo Electrónico:</label>
                <input name='email' value={inputs.email} onChange={handleChange} type='text' placeholder='Escribe tu email...'></input>
                {/* {errors.email && <p className='danger'>{errors.email}</p>} */}
                <label htmlFor='password'>Contraseña:</label>
                <textarea name='password' value={inputs.password} onChange={handleChange} type='text' placeholder='Escribe tu constraseña...'></textarea>
                {/* {errors.password && <p className='danger'>{errors.password}</p>} */}
                <button type='submit'>Enviar</button>
            </form>
        </div>
    )
}
