export default function Card(personaje) {

   return (

      <div style={{
         // backgroundColor: '#48C9B0',
         backgroundColor: 'white',
         margin: '5px',
         maxWidth: '20%',
         position: 'relative',
         justifySelf: 'center',
         borderRadius: 20,
         fontSize: '0.8em',
         opacity:'80%',
         border:'5px solid gray'

      }}>
         <button style={{
            position: 'absolute',
            right: 0,

         }} onClick={() => personaje.onClose()}>X</button>
         <img style={{
            margin: '5px',
            maxWidth: '90%',
            borderRadius: 20,
            marginTop: '10%'
         }} src={personaje.image} alt={personaje.name} />
         <div style={{
            display:'inline-block'
         }}>
            <h2>{personaje.name}</h2>
            <h2>{personaje.status}</h2>
            <h2>{personaje.species}</h2>
            <h2>{personaje.gender}</h2>
            <h2>{personaje.origin.name}</h2>
         </div>

      </div>
   );
}
