const { LOGIN } = require('../actions/types');
const inicialState = {
    sesion: {
        email: "ing.rafaelprieto@gmail.com",
        password: Rafael1234,
        status: false,
    }
}


const rootReducer = (state = inicialState, actions) => {
    switch (actions.type) {
        case LOGIN:
            let sesion = (actions.payload.email === state.email
                && actions.payload.password === state.password) ? true : false;
            return {
                ...state,
                status: sesion,
            }

        case LOGOUT:
            return {
                ...state,
                status: false,
            }

        default: return {...state }
    }
}
